import React, { useState } from "react";
import { addStyles, EditableMathField, StaticMathField } from "react-mathquill";
import nerdamer from 'nerdamer'
addStyles();

var a=nerdamer("x+3=5")
console.log(a)

class Sheet extends React.Component {
  // constructor has states with sheet info?
 constructor(props){
     super(props)
     this.state= {
         SoEs:[
         {
             name:"SoE 1",
             eqns: [
                 {
                     eqn:"x+4=5",
                     operation: "solve",
                     solvefor: "x",
                     result:"", // this would be computed: x=1
                 },
                 {
                     eqn:"a+3=b",
                     operation: "substitute",
                     substitutefor: {b: "x"},
                     result: "" // this would be computed: a+3=x
                 }
             ]
         },
         {
             name:"SoE 2",
             eqns: [
                 {
                     eqn:"SoE 1",
                     operation: "solve",
                     solvefor: "a",
                     result: "cool result!", // this would be computed: a=x-3=1-3=-2 (not sure how many steps to show)
                 },
             ]
         }
     ]
     }
 }


 inputEqnUpdate(i,j,eqnStruct){this.state.SoEs[i].eqns[j]=eqnStruct}

 updateSheet(){
   // no inputs
   // access this.state.SoEs
   // 
 }

 render(){
     var sheetElements=[]
     for (let i=0;i<this.state.SoEs.length;i++){
         var SoE=this.state.SoEs[i]
         //this.SoEs[i].name=newName

         sheetElements.push(<Name 

             onEdit={(newName)=>
                 {
                    // updating one part of SoEs state (likely better approach):
                    this.state.SoEs[i].name=newName
                    this.setState({SoEs:[...this.state.SoEs]})
                 }
             } 
             text={SoE}
         />)



         for (let j=0;j<SoE.eqns.length;j++){
            sheetElements.push(<Line 
              onEdit={(newEqn)=>
                {
                  console.log(this.state.SoEs)
                  this.state.SoEs[i].eqns[j].eqn=newEqn
                  this.setState({SoEs:[...this.state.SoEs]})


                }
              }
              eqnStruct={SoE.eqns[j]}

            />)
         }
     }
     return(
         sheetElements
     )

 }
}

class Name extends React.Component {
 render(){
     //const name=<tr><td></td></tr>
     const editField=<input 
         type="text" 
         value={this.props.text.name} 
         onChange={(e)=>{this.props.onEdit(e.target.value)
         }

     }
     >
     </input>

     return <tr><td>{editField}</td></tr>
 } 
}

class Line extends React.Component { 
  render() {
     const eqnStruct=this.props.eqnStruct
     console.log(eqnStruct.result)
     const line=<tr> 
         <td>
            <EditableMathField 
              latex=""  
              onChange={(mathField)=>{this.props.onEdit(mathField.latex())
              }       
              }

            />

         </td>
         <td>
             <StaticMathField>{"\\text{".concat(eqnStruct.result).concat("}")}</StaticMathField>
         </td>
     </tr>


      return line
 }
}

export default Sheet
