                     result: "" // this would be computed: a+3=x
                 }
             ]
         },
         {
             name:"SoE 2",
             eqns: [
                 {
                     eqn:"SoE 1",
                     operation: "solve",
                     solvefor: "a",
                     result: "cool result!", // this would be computed: a=x-3=1-3=-2 (not sure how many steps to show)
                 },
             ]
         }
     ]
     }
 }


 inputEqnUpdate(i,j,eqnStruct){this.state.SoEs[i].eqns[j]=eqnStruct}

